def get_test_sequence(mod: str, sequence_type: str = "nucl", sequence_name: str = None) -> dict:
    """
    Get appropriate test sequence for a given MOD and sequence type.
    
    Args:
        mod: MOD identifier (SGD, WB, FB, RGD, ZFIN, MGI)
        sequence_type: 'nucl' or 'prot'  
        sequence_name: specific sequence name (optional)
    
    Returns:
        Dictionary with sequence and description
    """
    # First try MOD-specific sequences
    if mod in MOD_SPECIFIC_SEQUENCES and sequence_type in MOD_SPECIFIC_SEQUENCES[mod]:
        return MOD_SPECIFIC_SEQUENCES[mod][sequence_type]
    
    # Fall back to universal sequences
    if sequence_type == "nucl":
        if sequence_name and sequence_name in UNIVERSAL_NUCLEOTIDE_SEQUENCES:
            return UNIVERSAL_NUCLEOTIDE_SEQUENCES[sequence_name]
        return UNIVERSAL_NUCLEOTIDE_SEQUENCES["18S_rRNA"]  # Default
    else:
        if sequence_name and sequence_name in UNIVERSAL_PROTEIN_SEQUENCES:
            return UNIVERSAL_PROTEIN_SEQUENCES[sequence_name]
        return UNIVERSAL_PROTEIN_SEQUENCES["cytochrome_c"]  # Default

def get_all_test_sequences(mod: str) -> dict:
    """Get all available test sequences for a MOD."""
    result = {"nucl": [], "prot": []}
    
    # Add universal sequences
    for name, seq_data in UNIVERSAL_NUCLEOTIDE_SEQUENCES.items():
        result["nucl"].append({
            "name": name,
            "sequence": seq_data["sequence"],
            "description": seq_data["description"]
        })
    
    for name, seq_data in UNIVERSAL_PROTEIN_SEQUENCES.items():
        result["prot"].append({
            "name": name,
            "sequence": seq_data["sequence"],
            "description": seq_data["description"]
        })
    
    # Add MOD-specific sequences if available
    if mod in MOD_SPECIFIC_SEQUENCES:
        if "nucl" in MOD_SPECIFIC_SEQUENCES[mod]:
            result["nucl"].insert(0, {
                "name": f"{mod}_specific",
                "sequence": MOD_SPECIFIC_SEQUENCES[mod]["nucl"]["sequence"],
                "description": MOD_SPECIFIC_SEQUENCES[mod]["nucl"]["description"]
            })
        if "prot" in MOD_SPECIFIC_SEQUENCES[mod]:
            result["prot"].insert(0, {
                "name": f"{mod}_specific", 
                "sequence": MOD_SPECIFIC_SEQUENCES[mod]["prot"]["sequence"],
                "description": MOD_SPECIFIC_SEQUENCES[mod]["prot"]["description"]
            })
    
    return result